import React from "react";
import LotteryCard from "../components/LotteryCard";

function Home() {
  return (
    <div className="home">
      <header className="hero">
        <h1>Welcome to Zottry</h1>
        <p>Your chance to win daily, weekly, and monthly draws!</p>
      </header>

      <section className="lottery-section">
        <LotteryCard title="Zottry Spark" price="$1" frequency="Daily Draw" prize="$1,000" />
        <LotteryCard title="Zottry Blaze" price="$2.5" frequency="Every 5 Days" prize="$100,000" />
        <LotteryCard title="Zottry Legend" price="$5" frequency="Monthly (Twice)" prize="$600,000" />
      </section>
    </div>
  );
}

export default Home;
