import React from "react";

function HowToPlay() {
  return (
    <div className="page">
      <h1>How to Play</h1>
      <p>Choose your lottery, select your numbers (or Quick Pick), add to cart, and purchase tickets securely.</p>
      <ol>
        <li>Select your game (Spark, Blaze, Legend).</li>
        <li>Pick numbers or choose Quick Pick.</li>
        <li>Add to cart and checkout.</li>
        <li>Wait for the draw and check results!</li>
      </ol>
    </div>
  );
}

export default HowToPlay;
