import React from "react";

function Rules() {
  return (
    <div className="page">
      <h1>Rules & Regulations</h1>
      <p>Stay informed about the guidelines that keep every Zottry draw fair and transparent.</p>
      <ul>
        <li>Players must be 18 years or older to participate.</li>
        <li>Tickets are non-refundable once a draw has closed.</li>
        <li>Winners must claim prizes within 90 days of the draw.</li>
        <li>Zottry reserves the right to verify winning entries before payout.</li>
      </ul>
    </div>
  );
}

export default Rules;
